&quot;).decode() for i in range(15): # io.recvuntil(b&quot;	&quot; + bytearray([i+ord('a')])) r = io.recvuntil(b&quot;
&quot;).decode() raw += r a = r.strip() checkerboard.append(a[3::2]) # print(checkerboard) return scoreboard, checkerboard, rawkey = &quot;abcdefghijklmno&quot;def check_pos(x, y, checkerboard: []): res = 0 cnt = 0 for i in range(max(0, x-4), min(14, x+4)+1): cnt += checkerboard[i][y] == 'X' res = max(res, cnt) cnt = 0 for i in range(max(0, y - 4), min(14, y + 4)+1): cnt += checkerboard[x][i] == 'X' res = max(res, cnt) cnt = 0 for i in range(-4, 4+1): if 0 &lt;= x + i &lt;= 14 and 0 &lt;= y + i &lt;= 14: cnt += checkerboard[x+i][y+i] == 'X' res = max(res, cnt) cnt = 0 for i in range(-4, 4+1): if 0 &lt;= x - i &lt;= 14 and 0 &lt;= y + i &lt;= 14: cnt += checkerboard[x-i][y+i] == 'X' res = max(res, cnt) return resdef get_nice_pos(checkerboard): p = [] for i in range(15): for j in range(15): if checkerboard[i][j] not in &quot;.+&quot;: continue p.append((check_pos(i, j, checkerboard), i, j)) p.sort(reverse=True) return p[0][1:]def action(checkerboard: []): print(len(checkerboard), end=&quot; &quot;) for x in checkerboard: print(len(x), end=&quot; &quot;) print() while True: # x = random.randint(0, 14) # y = random.randint(0, 14) # print(x, y) x, y = get_nice_pos(checkerboard) if checkerboard[x][y] in ['.', '+']: return key[y] + key[x]for i in range(100): try: s, c, r = getinfo() log.success(f&quot;Scoreboard {s}&quot;) log.success(f&quot;Checkerboard 
