rac(frac) for (k, d) in convergents: # check if d is actually the key if k != 0 and (e * d - 1) % k == 0: phi = (e * d - 1) // k s = n - phi + 1 # check if the equation x^2 - s*x + n = 0 # has integer roots discr = s * s - 4 * n if (discr &gt;= 0): t = Arithmetic.is_perfect_square(discr) if t != -1 and (s + t) % 2 == 0: print(&quot;Hacked!&quot;) return ddef attack(n, e): d = hack_RSA(e, n * n) k = (e * d - 1 + n * n) // (n * n) tmp = (n * n) - ((e * d - 1) // k) + 1 + 2 * n ppq = int(iroot(tmp, 2)[0]) tmp = (n * n) - ((e * d - 1) // k) + 1 - 2 * n pmq = int(iroot(tmp, 2)[0]) p = int(gcd(ppq + pmq, n)) q = n // p assert p * q == n assert e*d % ((p-1) * (q-1)) == 1 return p, q, dss = &quot;&quot;&quot;&quot;&quot;&quot;.strip().split()arr = list(map(int, ss))xx = []for x in range(7): n = arr[2*x] e = arr[2*x + 1] xx.append(attack(n, e))print(xx)#global_bits = 1024from extend_mt19937_predictor import ExtendMT19937Predictorfor p, q, d in xx: print(hex(p &amp; 0xffffffff), hex(q &amp; 0xffffffff), hex(d &amp; 0xffffffff)) print(p.bit_length(), q.bit_length(), d.bit_length())print(len(xx))for xt in range(2 ** 7): try: predictor = ExtendMT19937Predictor() for i in range(7): if xt &amp; (1 &lt;&lt; i): predictor.setrandbits(xx[i][0] &gt;&gt; 32, global_bits - 32) predictor.setrandbits(xx[i][1] &gt;&gt; 32, global_bits - 32) else: predictor.setrandbits(xx[i][1] &gt;&gt; 32, global_bits - 32) predictor.setrandbits(xx[i][0] &gt;&gt; 32, global_bits - 32) predictor.setrandbits(xx[i][2] &gt;&gt; 32, global_bits - 32 - 32) # d = predictor.predict_getrandbits(global_bits - 32) # print(d) break except ValueError: print(xt) del predictorfrom gmpy2 import next_prime, invert as inverse_modfrom Crypto.Cipher import PKCS1_v1_5from Crypto.PublicKey import RSAfrom random import getrandbitsfrom math import lcmfrom sys import exitdef generate_prime(bits: int): p = (predictor.predict_getrandbits(bits - 32) &lt;&lt; 32) return next_prime(p)def generate_private_key(bits: int): q, p = generate