## 题目

题目源码如下

```C
// gcc houseofsome.c -o houseofsome -Wl,--dynamic-linker=./ld-linux-x86-64.so.2 -Wl,--rpath=./
#include<stdio.h>
#include <stdlib.h>
#include<sys/mman.h>
#include <linux/seccomp.h>
#include <stdio.h>
#include <stdlib.h>
#include <sys/prctl.h>

static void install_seccomp() {
  static unsigned char filter[] = {32,0,0,0,4,0,0,0,21,0,0,5,62,0,0,192,32,0,0,0,0,0,0,0,53,0,3,0,0,0,0,64,21,0,2,0,59,0,0,0,21,0,1,0,66,1,0,0,6,0,0,0,0,0,255,127,6,0,0,0,0,0,0,0};
  struct prog {
    unsigned short len;
    unsigned char *filter;
  } rule = {
    .len = sizeof(filter) >> 3,
    .filter = filter
  };
  if(prctl(PR_SET_NO_NEW_PRIVS, 1, 0, 0, 0) < 0) { perror("prctl(PR_SET_NO_NEW_PRIVS)"); exit(2); }
  if(prctl(PR_SET_SECCOMP, SECCOMP_MODE_FILTER, &rule) < 0) { perror("prctl(PR_SET_SECCOMP)"); exit(2); }
}

int init() {
    size_t tmp1 = stdin;
    setbuf(tmp1, 0);
    size_t tmp2 = stdout;
    setbuf(tmp2, 0);
    size_t tmp3 = stderr;
    setbuf(tmp3, 0);
    install_seccomp();
}

size_t getint(){
    size_t tmp;
    scanf("%lld", &tmp);
    return tmp;
}

void readline(char *buf, size_t n) {
    char tmp;
    size_t i = 0;
    for(; i < n && read(0, &tmp, 1) > 0; ++i) {
        if(tmp == '\n') {
            buf[i] = 0;
            return;
        }
        buf[i] = tmp;
    }
    buf[i] = 0;
} 

char *name = 0;
FILE* dev = 0;
int magic = 0;
char *pool[2] = {0};
int idx = 1;

void change_name(){
    char buf[0x100];
    size_t size;
    printf("size> ");
    size = getint();
    if(size <= 0 || size > 0x2000) {
        puts("wrong.");
        return;
    }
    idx = 1 - idx;
    name = pool[idx];
    if(name) free(name);
    printf("name> ");
    name = malloc(size + 1);
    pool[idx] = name;
    readline(name, size);
}

void change_dev() {
    char buf[0x100];
    if(dev) fclose(dev);
    size_t op;
    printf("1. /dev/urandom\n2. /dev/zero\n3. /dev/null\ndev> ");
    op = getint();
    if(op == 1) dev = fopen("/dev/urandom", "rb");
    else if(op == 2) dev = fopen("/dev/zero", "rb");
    else dev = fopen("/dev/null", "rb");
    if(dev == NULL){
        puts("open dev error.");
        return;
    }
    setbuf(dev, 0);
}

void draw() {
    char buf[0x100];
    if(magic || !dev || !name) {
        puts("wrong.");
        return;
    }
    size_t addr, length;
    printf("offset> ");
    addr = getint();
    printf("length> ");
    length = getint();
    if(length < 0 || length > 8) {
        puts("wrong.");
        return;
    }
    fread(0x114514000+addr, 1, 1, dev);
    magic = 1;
}

void show() {
    char buf[0x100];
    printf("name: %s\n", name);
    printf("picture: ");
    write(1, 0x114514000, 0x1000);
    printf("\n");
}

int main(){
    init();
    size_t choice;
    if(mmap(0x114514000, 0x1000, PROT_READ | PROT_WRITE, 0x22, -1, 0) == -1){
        printf("mmap error.\n");
        exit(0);
    }
    printf("gift: %p\n", 0x114514000);
    while (1) {
        printf("1. name\n2. dev\n3. draw\n4 .show\n5. exit\n> ");
        choice = getint();
        switch (choice){
            case 1:
                change_name();
                break;
            case 2:
                change_dev();
                break;
            case 3:
                draw();
                break;
            case 4:
                show();
                break;  
            case 5:
                exit(0);
                break;
            default:
                printf("invalid option %ld.\n", choice);
                break;
        }
    }

}
