environ = libc.symbols['__environ']
name(0xb0-1, flat({
    0x00: 0, # _flags
    0x20: 0, # _IO_write_base
    0x28: 0, # _IO_write_ptr

    0x38: environ+8, # _IO_buf_base
    0x40: environ+8+0x400, # _IO_buf_end

    0x70: 0, # _fileno
    0x68: environ+8, # _chain
    0x82: b"\x00", # _vtable_offset
    0x88: environ-0x10,
